import React from 'react';
import { Container, Row, Col, Card, ListGroup, Button, Badge } from 'react-bootstrap';
import { PlusCircle, Link45deg, Clock, Person } from 'react-bootstrap-icons';

const ProjectsList = ({ isAdmin = false, projects = [] }) => {
    // Default projects if none provided
    const defaultProjects = [
        {
            id: 1,
            name: 'JitendraArya-Zenith',
            client: 'Jitendra',
            lastViewed: '2 hours ago',
            links: 3
        },
        {
            id: 2,
            name: 'E-Commerce Platform',
            client: 'Acme Corp',
            lastViewed: '1 day ago',
            links: 5
        },
    ];

    const displayProjects = projects.length ? projects : defaultProjects;

    return (
        <Container className="py-4">
            <Row className="mb-4 align-items-center">
                <Col>
                    <h2 className="mb-0">
                        <Clock className="me-2" />
                        Recently Viewed Projects
                    </h2>
                </Col>
                {isAdmin && (
                    <Col xs="auto">
                        <Button variant="success" className="shadow-sm">
                            <PlusCircle className="me-2" />
                            New Project
                        </Button>
                    </Col>
                )}
            </Row>

            <Card className="shadow-sm">
                <ListGroup variant="flush">
                    {displayProjects.map((project) => (
                        <ListGroup.Item key={project.id} action className="py-3">
                            <Row className="align-items-center">
                                <Col md={6} className="mb-2 mb-md-0">
                                    <h5 className="mb-1">{project.name}</h5>
                                    <div className="d-flex flex-wrap gap-2">
                                        <Badge bg="light" text="dark" className="d-flex align-items-center">
                                            <Person className="me-1" />
                                            {project.client}
                                        </Badge>
                                        <Badge bg="light" text="dark">
                                            <Clock className="me-1" />
                                            {project.lastViewed}
                                        </Badge>
                                    </div>
                                </Col>
                                <Col md={6} className="d-flex justify-content-md-end">
                                    <Button
                                        variant="outline-primary"
                                        className="d-flex align-items-center"
                                    >
                                        <Link45deg className="me-1" />
                                        View Links ({project.links})
                                    </Button>
                                </Col>
                            </Row>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            </Card>
        </Container>
    );
};

export default ProjectsList;
