import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Form, Button, Card, Container, FloatingLabel } from 'react-bootstrap';
import { FaSignInAlt, FaLock, FaEnvelope, FaUserPlus } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            await login(email, password);
            navigate('/dashboard');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container className="login-container">
            <Card className="login-card shadow-lg">
                <Card.Body className="p-5">
                    <div className="text-center mb-5">
                        <div className="login-logo mb-4">
                            <img src="/logo.svg" alt="Alps CRM" width="120" />
                        </div>
                        <h2 className="fw-bold mb-2">Welcome Back</h2>
                        <p className="text-muted">Sign in to your Alps CRM account</p>
                    </div>

                    <Form onSubmit={handleSubmit}>
                        <FloatingLabel controlId="email" label="Email address" className="mb-4">
                            <Form.Control
                                type="email"
                                placeholder="name@example.com"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                                className="py-3"
                            />
                            <FaEnvelope className="input-icon" />
                        </FloatingLabel>

                        <FloatingLabel controlId="password" label="Password" className="mb-4">
                            <Form.Control
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                className="py-3"
                            />
                            <FaLock className="input-icon" />
                        </FloatingLabel>

                        <div className="d-flex justify-content-between mb-4">
                            <Form.Check type="checkbox" label="Remember me" />
                            <Link to="/forgot-password" className="text-decoration-none">
                                Forgot password?
                            </Link>
                        </div>

                        <Button
                            variant="primary"
                            type="submit"
                            size="lg"
                            className="w-100 py-3 fw-bold"
                            disabled={loading}
                        >
                            {loading ? 'Signing in...' : (
                                <>
                                    <FaSignInAlt className="me-2" />
                                    Sign In
                                </>
                            )}
                        </Button>

                        <div className="text-center mt-4">
                            <p className="text-muted">
                                Don't have an account?{' '}
                                <Link to="/register" className="fw-bold text-primary text-decoration-none">
                                    <FaUserPlus className="me-1" />
                                    Create Account
                                </Link>
                            </p>
                        </div>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
};

export default Login;
