import $ from "jquery";
const token = localStorage.getItem('token');
export async function createProjectApi(projectData) {
    $(".loader").show();
    try {
        // ðŸ‘‰ Get token from localStorage
        // const token = localStorage.getItem("token");
        const response = await fetch("http://localhost:5000/api/project/create", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                // ðŸ‘‰ Add Authorization header if token exists
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify(projectData),
        });
        const data = await response.json();
        if (!response.ok || data.status === false) {
            throw new Error(data.message || "Something went wrong");
        }
        return data;
    } finally {
        $(".loader").hide();
    }
}
export async function getAllProjectApi() {
    $(".loader").show();
    try {
        // ðŸ‘‰ Get token from localStorage
        // const token = localStorage.getItem("token");
        const response = await fetch("http://localhost:5000/api/project/create", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                // ðŸ‘‰ Add Authorization header if token exists
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify(projectData),
        });
        const data = await response.json();
        if (!response.ok || data.status === false) {
            throw new Error(data.message || "Something went wrong");
        }
        return data;
    } finally {
        $(".loader").hide();
    }
}
