import React from 'react';
import './Navbar.css'; // For custom styles that override/extend Bootstrap
import { Container, Navbar, Button } from 'react-bootstrap';
import { Bell, List } from 'react-bootstrap-icons';

const NavBar = () => {
    const isAdmin = sessionStorage.getItem('role');
    return (
        <Navbar bg="primary" variant="dark" fixed="top" className="alps-navbar">
            <Container fluid>
                <Navbar.Brand href="#" className="alps-logo">ALPSCRM</Navbar.Brand>

                <div className="d-flex align-items-center">
                    (isAdmin==='ADMIN' && (
                        <Button variant="success" className="create-btn me-2">
                            Create
                        </Button>
                    ))
                    <Button variant="link" className="notification-icon me-2">
                        <Bell size={20} />
                    </Button>
                    <Button variant="link" className="menu-icon">
                        <List size={20} />
                    </Button>
                </div>
            </Container>
        </Navbar>
    );
};

export default NavBar;
